import React, { useState, useEffect } from "react";
import "./App.css";
import TrainingDataForm from "./components/TrainingDataForm";
import Predictions from "./components/Predictions";

function App() {
  const [trainingData, setTrainingData] = useState([]);
  const [predictions, setPredictions] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Load template data when component mounts
    fetchTemplate();
  }, []);

  const fetchTemplate = async () => {
    try {
      const response = await fetch("http://localhost:5001/api/template");
      const data = await response.json();
      setTrainingData(data);
    } catch (err) {
      setError("Failed to load template data");
      console.error(err);
    }
  };

  const handleSubmit = async (data) => {
    setLoading(true);
    setError(null);
    try {
      // Send only the current row's data
      const currentRow = data.find(row => row.week === 1 && row.lift_type === "Squat");
      const response = await fetch("http://localhost:5001/api/predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(currentRow),
      });

      if (!response.ok) {
        throw new Error("Failed to get predictions");
      }

      const result = await response.json();
      setPredictions(result);
    } catch (err) {
      setError("Failed to get predictions");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Powerlifting Meet Predictor</h1>
      </header>
      <main>
        <TrainingDataForm
          data={trainingData}
          onSubmit={handleSubmit}
          loading={loading}
        />
        {error && <div className="error">{error}</div>}
        {predictions && <Predictions predictions={predictions} />}
      </main>
    </div>
  );
}

export default App;
